"""
Unit tests for barcode detector classes.

Each detector has:
- test_init: Basic initialization
- test_no_match_random_sequence: Random sequence with no expected match
- test_real_sequence: Placeholder for real data (TODO: add real sequences)
"""

import pytest
from src.barcode_calling.callers import (
    # Stereo-seq
    StereoBarcodeDetector,
    StereoSplittingBarcodeDetector,
    SharedMemoryStereoBarcodeDetector,
    SharedMemoryStereoSplittingBarcodeDetector,
    # 10x Genomics
    TenXBarcodeDetector,
    VisiumHDBarcodeDetector,
    # Curio
    CurioBarcodeDetector,
    CurioIlluminaDetector,
)
from src.barcode_calling.callers.base import (
    SplittingBarcodeDetectionResult,
    TSOBarcodeDetectionResult,
    TenXBarcodeDetectionResult,
    LinkerBarcodeDetectionResult,
)


# =============================================================================
# Dummy data for testing
# =============================================================================

# Random sequence with no expected barcode match (just random nucleotides)
RANDOM_SEQUENCE = (
    "GCTAGCTAGCTAGCTAGCTAGCTAGCTAGCTAGCTAGCTAGCTAGCTAGCTAGCTAGCTAGCTA"
    "ATCGATCGATCGATCGATCGATCGATCGATCGATCGATCGATCGATCGATCGATCGATCGATCG"
    "CGATCGATCGATCGATCGATCGATCGATCGATCGATCGATCGATCGATCGATCGATCGATCGAT"
)

# Dummy barcodes for initializing indexers
DUMMY_BARCODES_16 = [
    "ACTGACTGACTGACTG",
    "TGCATGCATGCATGCA",
    "GGGGGGGGGGGGGGGG",
]

DUMMY_BARCODES_25 = [
    "ACTGACTGACTGACTGACTGACTGA",
    "TGCATGCATGCATGCATGCATGCAT",
    "GGGGGGGGGGGGGGGGGGGGGGGGG",
]

DUMMY_BARCODES_14 = [
    "ACTGACTGACTGAC",
    "TGCATGCATGCATG",
    "GGGGGGGGGGGGGG",
]

DUMMY_BARCODES_8 = [
    "ACTGACTG",
    "TGCATGCA",
    "GGGGGGGG",
]

DUMMY_BARCODES_6 = [
    "ACTGAC",
    "TGCATG",
    "GGGGGG",
]

DUMMY_BARCODES_15 = [
    "ACTGACTGACTGACT",
    "TGCATGCATGCATGC",
    "GGGGGGGGGGGGGGG",
]


# =============================================================================
# Stereo-seq Detector Tests
# =============================================================================

class TestStereoBarcodeDetector:
    """Test Stereo-seq barcode detector."""

    def test_init(self):
        """Test basic initialization."""
        detector = StereoBarcodeDetector(DUMMY_BARCODES_25, min_score=21)
        assert detector.barcode_indexer is not None
        assert detector.min_score == 21

    def test_init_empty_barcodes(self):
        """Test initialization with empty barcode list."""
        detector = StereoBarcodeDetector([], min_score=21)
        assert detector.barcode_indexer is None

    def test_no_match_random_sequence(self):
        """Test that random sequence returns no valid barcode."""
        detector = StereoBarcodeDetector(DUMMY_BARCODES_25, min_score=21)
        result = detector.find_barcode_umi("test_read", RANDOM_SEQUENCE)

        assert isinstance(result, (LinkerBarcodeDetectionResult, TSOBarcodeDetectionResult))
        assert result.read_id == "test_read"
        # Random sequence should not match any barcode
        assert not result.is_valid()

    @pytest.mark.parametrize("read_sequence, true_barcode, true_umi",
                             [("CTTCCGATCTATGGCGACCTCAGGAAGGAGGGATCCATAACCGGCCCCATTGTCTTCCTAAGACATTTATCCGATTTTTTTTTTTTTTTTTTTTTTTTTTTTTTTC"
                               "AAAACTAGCTTTTGTGCTGGGTAATTTTATTTTTTTAAGAAATGGGGGAAAGCTAAAAAGAGAGCGTCAGGGTC",
                               "GAAGGAGGGATCCATAACGGCCCCA", "ATTTATCCGA"),
                              ("TTCCGATCTATGGCGACCTTATCAGTCCCCACTTTCACTCTCACCCCATTTTGTCTTCCTAAGACCTAGTGTAGCTTTTTTTTTTTTTTTTTTTTTTTTTTTTTT"
                               "CACATATAAATATGACTTTATTAACCAGTCTGTGTTTGATTTAGCACTCTAGTGGCTATCAGTGATTTTCTTTTA",
                               "TCCCCACTTTCACTCTCACCCCATT", "CTAGTGTAGC"),
                              ("TTCCGATCTATGGCGACCTTATCAGAGTTGGCGCCCAAATGGTCAGAGGGTTGTCTTCCTAAGACACGCAATCACTTTTTTTTTTTTTTTTTTTTTTTTTTTTTT"
                               "GCAGACATCAGTAGTTTATTGTTTTAGTCCAAACACATTCAAAATGGAAACTCAAAAGAATACTTTCTACCTGAA",
                               "AGTTGGCGCCCAAATGGTCAGAGGG", "ACGCAATCAC"),
                              ("ATCTTTCTGATATCACTGTCTACCCATTATTAAAAGAAAATCACTGATAGCCACTAGAGTGCTAAATCAAACACAGACTGGTTAATAAAGTCATATTTATATGTG"
                               "AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAGCTACACTAGGTCTTAGGAAGACAAAATGGGGTGAGAGTGAAAGTGGGGACTGATAAGGTCGCCATAGATCGGAAG",
                               "TCCCCACTTTCACTCTCACCCCATT", "CTAGTGTAGC"),
                              ("TTCCGATCTATGGCGACCTTATCAGTCCCCACTTTCACTCTCACCCCATTTTGTCTTCCTAAGACCTAGTGTAGCTTT",
                               "TCCCCACTTTCACTCTCACCCCATT", "CTAGTGTAGC"),
                              ("TTCCGATCTATGGCGACCTTATCAGAGTTGGCGCCCAAATGGTCAGAGGGTTGTCTTCCTAAGACACGCAATCACTT",
                               "AGTTGGCGCCCAAATGGTCAGAGGG", "ACGCAATCAC"),
                              ("AGCTACACTAGGTCTTAGGAAGACAAAATGGGGTGAGAGTGAAAGTGGGGACTGATAAGGTCGCCATAGATCGGAAG",
                               "TCCCCACTTTCACTCTCACCCCATT", "CTAGTGTAGC"),
                              ])
    def test_real_sequence(self, read_sequence, true_barcode, true_umi):
        # Test with real sequence data.

        barcodes = ["GAAGGAGGGATCCATAACGGCCCCA",
                    "TCCCCACTTTCACTCTCACCCCATT",
                    "AGTTGGCGCCCAAATGGTCAGAGGG",
                    "CATCAAAGGTTCACCGAATCTCCAC",
                    "GTCGCCTTCAACGTATACTGATATA",
                    "ACCAATTAGCAGCCATGGGAACTCC"]
        detector = StereoBarcodeDetector(barcodes, min_score=21)
        result = detector.find_barcode_umi("test_read", read_sequence)

        assert isinstance(result, (LinkerBarcodeDetectionResult, TSOBarcodeDetectionResult))
        assert result.read_id == "test_read"
        # Random sequence should not match any barcode
        assert result.is_valid()
        assert result.barcode == true_barcode
        assert result.UMI == true_umi


class TestStereoSplittingBarcodeDetector:
    """Test Stereo-seq splitting barcode detector."""

    def test_init(self):
        """Test basic initialization."""
        detector = StereoSplittingBarcodeDetector(DUMMY_BARCODES_25)
        assert detector.barcode_indexer is not None

    def test_init_empty_barcodes(self):
        """Test initialization with empty barcode list."""
        detector = StereoSplittingBarcodeDetector([])
        assert detector.barcode_indexer is None

    def test_no_match_random_sequence(self):
        """Test that random sequence returns empty splitting result."""
        detector = StereoSplittingBarcodeDetector(DUMMY_BARCODES_25)
        result = detector.find_barcode_umi("test_read", RANDOM_SEQUENCE)

        assert result.read_id == "test_read"
        # Should return empty or no valid patterns
        assert result.empty() or not any(p.is_valid() for p in result.detected_patterns)

    @pytest.mark.parametrize("read_sequence, true_barcodes, true_umis",
                             [("",
                               ("GGCAGGCCATTAGTTACTCGTCGAG", "CTCCCCTAATCGTTTGTCGGTGGAC", "GGGCACATTCCTCAGAAAGCGAAAA"),
                               ("TGAACCCGATTATG", "ACCTTCGCGC", "CGGCCACTGC")),
                              ("",
                               ("TATGTACGAATTTAACCGTACCCTC", "ACATGATCCTCCGTGGCATGCGCAC"),
                               ("CTTGCCGTCG", "TGAAAGCCCG")),
                              ])
    def test_real_sequence(self, read_sequence, true_barcodes, true_umis):
        # Test with real sequence data.

        barcodes = ["GAAGGAGGGATCCATAACGGCCCCA",
                    "TCCCCACTTTCACTCTCACCCCATT",
                    "AGTTGGCGCCCAAATGGTCAGAGGG",
                    "CATCAAAGGTTCACCGAATCTCCAC",
                    "GTCGCCTTCAACGTATACTGATATA",
                    "ACCAATTAGCAGCCATGGGAACTCC",
                    "ACATGATCCTCCGTGGCATGCGCAC",
                    "TATGTACGAATTTAACCGTACCCTC",
                    "GGGCACATTCCTCAGAAAGCGAAAA",
                    "GGCAGGCCATTAGTTACTCGTCGAG",
                    "CTCCCCTAATCGTTTGTCGGTGGAC"]
        detector = StereoSplittingBarcodeDetector(barcodes, min_score=21)
        result = detector.find_barcode_umi("test_read", read_sequence)

        assert isinstance(result, SplittingBarcodeDetectionResult)
        assert result.read_id == "test_read"
        # Random sequence should not match any barcode
        assert len(result.detected_patterns) == len(true_barcodes)
        for i, r in enumerate(result.detected_patterns):
            assert r.is_valid()
            assert r.barcode == true_barcodes[i]
            assert r.UMI == true_umis[i]


