class TestVisiumHDBarcodeDetector:
    """Test Visium HD spatial barcode detector."""

    def test_init(self):
        """Test basic initialization."""
        barcode_pairs = [DUMMY_BARCODES_16, DUMMY_BARCODES_15]
        detector = VisiumHDBarcodeDetector(barcode_pairs)

        assert detector.part1_barcode_indexer is not None
        assert detector.part2_barcode_indexer is not None
        assert detector.min_score == 13

    def test_no_match_random_sequence(self):
        """Test that random sequence returns no valid barcode."""
        barcode_pairs = [DUMMY_BARCODES_16, DUMMY_BARCODES_15]
        detector = VisiumHDBarcodeDetector(barcode_pairs)
        result = detector.find_barcode_umi("test_read", RANDOM_SEQUENCE)

        assert isinstance(result, TenXBarcodeDetectionResult)
        assert result.read_id == "test_read"
        assert not result.is_valid()

    @pytest.mark.parametrize("read_sequence, true_barcode, true_umi",
                             [("",
                               "GTCACTATACTGCAAG|AAGATCATGTGCAAG", "AATACCACA"),
                              ("",
                               "GTTGAGCATCCCCAG|ATATGTCATGCACTG", "TTCGGTACTC"),
                              ("",
                               "GCTTAAATGCATCAG|CTGATCAGCGCGAAG", "ATCGTTTTTA"),
                              ("TACACGACGCTCTTCCGATCTAATACCACAGTCACTAACCTGCAAGAAGATCATGTGCAAGACT",
                               "GTCACTATACTGCAAG|AAGATCATGTGCAAG", "AATACCACA"),
                              ("TACACGACGCTCTTCCGATCTTTCGGTACTCGTTGAGCATCCCCAGATATGTCATGCACTGCAT",
                               "GTTGAGCATCCCCAG|ATATGTCATGCACTG", "TTCGGTACT")
                              ])
    def test_real_sequence(self, read_sequence, true_barcode, true_umi):
        """Test with real Visium HD sequence data."""
        # BC1 (16bp) and BC2 (15bp) whitelists extracted from test data
        bc1_whitelist = [
            "GTCACTATACTGCAAG",
            "GTTGAGCATCCCCAG",
            "GCTTAAATGCATCAG",
            "GTCTTGTGTGCGCACG",
            "GTCGGCAGATCTATCG",
            "GTATGCTGCTTCCTAG",
            "GCATCATATCCGTCAG",
            "GTCATCTGACATTCTG",
        ]
        bc2_whitelist = [
            "AAGATCATGTGCAAG",
            "ATATGTCATGCACTG",
            "CTGATCAGCGCGAAG",
            "TAGATGGCAGCCAG",
            "TCGATATCTGTGTCG",
            "TTGCGCATTTATAG",
            "TCTCACGAGAGCTG",
            "TCATTTGCTCTGATG",
        ]

        detector = VisiumHDBarcodeDetector([bc1_whitelist, bc2_whitelist])
        result = detector.find_barcode_umi("test_read", read_sequence)

        assert isinstance(result, TenXBarcodeDetectionResult)
        assert result.read_id == "test_read"
        assert result.is_valid()
        assert result.barcode == true_barcode
        assert result.UMI == true_umi


